sp_widget.client_script:
function(spUtil, spModal,$rootScope, $scope) {
	/* widget controller */
	var c = this;
	//spUtil.addErrorMessage('Test Error');
	//spUtil.addInfoMessage('Test Info ' );
	//spModal.prompt('Test');
	console.log('Test Console Client');
	c.updateNote = function(updateType){
		c.server.get({
			action : 'updateNote',
			noteID : c.data.noteID,
			noteBody : c.data.note,
			noteTitle : c.data.title
		}).then(function(r){
			if(updateType == 'title')
				$rootScope.$emit('updateNoteTitle', c.data.title);
			else if(updateType == 'body')
				$rootScope.$emit('updateNoteBody', c.data.body);
	});
	}

	$rootScope.$on('selectNote', function(event, data){
		c.server.get({
			action : 'getNote',
			noteID : $rootScope.noteID 
		}).then(function(r){
			c.data.title = r.data.note.title;
			c.data.note = r.data.note.note;
			c.data.noteID = r.data.note.sys_id;
		});

	});

}

sp_widget.script:
(function() {
  /* populate the 'data' object */
  /* e.g., data.table = $sp.getValue('table'); */
	//console.log('Test Console Script');
	if(input && input.noteID){
		var note = new GlideRecord('x_snc_createnotes_note');

		if(note.get(input.noteID)){
			if(input.action == 'getNote'){
				data.note = {};
				$sp.getRecordDisplayValues(data.note, note, "title, note, sys_id");
			}
			else if(input.action == 'updateNote'){
				note.note = input.noteBody;
				note.title = input.noteTitle;
				note.update();
			}
		}
	}
})();
